label("{\small $B$}",B,S);
label("{\small $C$}",C,E);
label("{\small $D$}",D,N);
label("{\small $\alpha$}",(1.5,-0.2),N);
label("{\small $\alpha$}",(15.2,3.8),W);
label("{\small 180}",(5,0),S);
