draw(shift((0,0,1))*surface(sphere(1)),gray(0.8));
draw(O--P,dashed + red);
draw(P--X,red);
draw(O--T,dashed + red);

dot("$O$", O, SE, white);
dot("$P$", P, NW);
dot("$X$", X, S);
dot(T, red);
label("$T$", T, W);
